package im.zhaojun.zfile.config;import cn.dev33.satoken.interceptor.SaRouteInterceptor;import cn.dev33.satoken.router.SaRouter;import cn.dev33.satoken.stp.StpUtil;import org.springframework.context.annotation.Configuration;import org.springframework.web.servlet.config.annotation.InterceptorRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;@Configurationpublic class SaTokenConfigure implements WebMvcConfigurer {    @Override    public void addInterceptors(InterceptorRegistry registry) {        registry.addInterceptor(new SaRouteInterceptor((req, res, handler)->{            SaRouter.match("/admin/**", "/admin", StpUtil::checkLogin);        })).addPathPatterns("/**");    }}